import React from 'react';
import TodoItem from './TodoItem';

const TodoList = ({ todos }) => {
	let display;

	if (!todos) {
		display = 'none';
	} else {
		display = 'block';
	}

	const todoListStyle = {
		background: '#f4f4f4',
		width: '100%',
		height: '100%',
		padding: '1rem',
		display: display
	};

	return (
		<ul className={todoListStyle}>
			{todos.map((todo) => {
				return <TodoItem key={todo} todo={todo} />;
			})}
		</ul>
	);
};

export default TodoList;
