import React from 'react';

const AddTodo = ({ onAddTodo, onChangeNewTodo, query }) => {
	return (
		<div className="new-todo">
			<form>
				<input
					type="text"
					className="add-todo"
					onChange={onChangeNewTodo}
					value={query}
					autoFocus
					onSubmit={onAddTodo}
				/>
				<button className="btn" onClick={onAddTodo}>
					Add Todo
				</button>
			</form>
		</div>
	);
};

export default AddTodo;
